// conexao do front end com backend
import axios from 'axios';

export default function api(){
    const api = axios.create({
        baseUrl: 'http://localhost:3333',
        timeout: 1000,
        headers: {'Access-Control-Allow-Origin': 'XMLHttpRequest'}
    })
    return api;
}
