import React, { useState, useEffect} from 'react';
import Header from '../../components/header/header';
import api from '../../services/api';
import { FiSearch } from 'react-icons/fi';


import './style.css';
import '../../globalStyle/style.css';




export default function Product(){

    const [products, setProducts] = useState([]);

    useEffect(() => {
        Promise.all([
          api.get("/produtos"),       
        ]).then((response) => {
          for (const res of response) {
            const {
              data: {id, produto, estoque, valor_venda, status, unidade_comercial
                ,qtde_promocional, fator }
            } = res;
    
            setProducts((state) => [...state, { 
                id, produto, estoque, valor_venda, status, unidade_comercial
                ,qtde_promocional, fator 
             }]);
        console.log(products);

          }
        });
      }, []);



    return(
        <div className="content">
            <div className="container">
                <Header />
                <div className="products">
                    <div className="wrapper">
                        <p>Produtos</p>
                        <input type="text" placeholder="Codigo, descrição"></input>
                        {/* <button type="button" onClick={() => SearchProduct()}> */}
                            <FiSearch size={20} color="#ffffff" />
                        {/* </button> */}
                    </div>
                    {products.map((product, index) =>(
                        <div class="product" key={String(product?.id + index)}>
                            <p>ID: { product?.produto } </p>
                            <p>Descrição: </p>
                            <p>valor venda: 10.00</p>
                            <p>Estoque: 5</p>
                            <p>Fator: 1</p>
                            <p>QTD promo: 10</p>
                            <p>Valor promo:4</p>
                        </div>
                    ))}
                    
                   
                </div>
                
            </div>
        </div>
        
    );
    
}
